const Editproduct = () =>{
        let { productid } = useParams();
        let [data, pickData] = useState("");
        let[text, updatetext] = useState("Update");
      
        const getdata = () => {
          let url = "http://localhost:9999/product/" + productid;
          let postdata = { method: "PUT" };
          fetch(url, postdata)
            .then((responsedata) => responsedata.json())
            .then((response) => {
              pickData(response);
            });
        };
      
        const update = () => {
            updatetext
          window.location.href = "#/productlist";
        };
      
        useEffect(() => {
          getdata();
        }, [1]);
      
        return (
          <div className="container">
            <div className="row">
              <div className="col-lg-12 text-center mt-5 mb-3">
                <h2 className="text-primary">Edit Details</h2>
              </div>
      
              <div className="col-lg-3"></div>
              <div className="col-lg-6">
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th>Name: </th>
                      <td><input type="text" value={data.name} onChange={obj => pickName(obj.target.value)}/></td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <th>Email: </th>
                      <td><input type="text" value={data.name} onChange={obj => pickName(obj.target.value)}/> </td>
                    </tr>
                    <tr>
                      <th>Mobile no: </th>
                      <td><input type="text" value={data.name} onChange={obj => pickName(obj.target.value)}/></td>
                    </tr>
                    <tr>
                      <th> City: </th>
                      <td><input type="text" value={data.name} onChange={obj => pickName(obj.target.value)}/></td>
                    </tr>
      
                    <tr className="text-center">
                      <td colSpan={3}>
                        <button className="btn btn-danger" onClick={back}>
                          {text}
                        </button>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div className="col-lg-3"></div>
            </div>
          </div>
        );
      };

export default Editproduct;
