import { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import swal from 'sweetalert';

const Login = () => {
    let [email, pickEmail] = useState("");
    let [password, pickPassword] = useState("");
    let [btncontrol, updatebtncontrol] = useState(false);

    const loginCheck = ()=>{
      if( email != "" || password!= "")
      {
            toast("Please wait Processing....");
            update
            let url = "http://localhost:3333/auth";
            let logindata = { emailid: email, password: password };
            let postdata = {
              headers: { "Content-Type": "application/json" },
              method: "POST",
              body: JSON.stringify(logindata),
            };
        
            fetch(url, postdata)
              .then((response) => response.json())
              .then((userdata) => {
               toast("successfully logged in....");
             
                if (userdata.status == "SUCCESS") {
                  localStorage.setItem("tokenno", userdata.token);
                  localStorage.setItem("fullname", userdata.fullname);
                  localStorage.setItem("role", userdata.role);
                  window.location.reload();
                }
              });
        }
        else{
            swal("Invalid Input..", "Please enter email & password", "warning");
        }
    }
  return (
    <section className="fullpage bg-info">
         <ToastContainer/>
      <div className="container">
        <div className="row">
          <div className="col-lg-4"></div>
          <div className="col-lg-4">
            <div className="card mt-5 shadow-lg border-0">
              <div className="card-header">
                {" "}
                <h5>Client Login</h5>
              </div>
              <div className="card-body">
                <div className="mb-4">
                  <label>Email Id</label>
                  <input type="text" className="form-control" onChange={obj=> pickEmail(obj.target.value)}/>
                </div>

                <div className="mb-4">
                  <label>Password</label>
                  <input type="text" className="form-control" onChange={obj => pickPassword(obj.target.value)}/>
                </div>
              </div>
              <div className="card-footer text-center">
                <button className="btn btn-danger" onClick={loginCheck}>Login</button>
              </div>
            </div>
          </div>
          <div className="col-lg-4"></div>
        </div>
      </div>
    </section>
  );
};

export default Login;
