import {useState, useEffect} from 'react';

const Mybilling = () =>{
    let [Customers, customerlist] = useState([]);
    let [Products, productlist] = useState([]);
  
    const getallcustomer = () => {
      let url =
        "http://localhost:9999/customer/" + localStorage.getItem("tokenid");
      fetch(url)
        .then((responsedata) => responsedata.json())
        .then((response) => {
          customerlist(response);
        });
    };
  
    const getallproduct = () => {
      let url =
        "http://localhost:9999/product";
      fetch(url)
        .then((responsedata) => responsedata.json())
        .then((response) => {
          productlist(response);
        });
    };
  
    useEffect(() => {
      getallcustomer();
      getallproduct();
    }, [1]);

  let [name, pickName] = useState("");
  let [email, pickEmail] = useState("");
  let [mobile, pickMobile] = useState("");
  let [city, pickCity] = useState("");
  let [pincode, pickPincode] = useState("");
  let [address, pickAddress] = useState("");

  const printCustomer = (cinfo) =>{
      pickName(cinfo.name);
      pickEmail(cinfo.email);
      pickMobile(cinfo.mobile);
      pickCity(cinfo.city);
      pickPincode(cinfo.address);
      pickAddress(cinfo);
  }

