    <div className="container mt-5">
         <div className="row">
            <div className="col-lg-12 text-center">
                <h2 className="text-primary"> Billing Management </h2>
            </div>

            <div className="col-lg-2">
                <label> Select Customer</label>
                <select className='form-control'>
                    <option> Choose </option>
                      {
                         Customers.map((customer,index)=>{
                              return(
                                 <option key={index}  onClick={printCustomer.bind(this, customer)}> {customer.name} </option>
                              )
                         })
                      }
                </select>
            </div>

            <div className="col-lg-2">
                <label> Select Product</label>
                <select className='form-control'>
                    <option> Choose </option>
                      {
                         Products.map((product,index)=>{
                              return(
                                 <option key={index} onClick={printProduct.bind(this, product)} > {product.name} </option>
                              )
                         })
                      }
                </select>
            </div>

            <div className="col-lg-2">
                <label> Enter Quantity </label>
               <input type="number"  className='form-control' />
            </div>

            <div className="col-lg-2">
                <label> Total Price </label>
               <input type="number"  className='form-control' />
            </div>

            <div className="col-lg-3 pt-4 text-center">
               <button className='btn btn-danger'> Generate Bill </button>
            </div>

         </div>
     </div>
    )
}

export default Mybilling;
