import { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import swal from "sweetalert";

const NewCustomer = () => {
  let [name, pickName] = useState("");
  let [email, pickEmail] = useState("");
  let [mobile, pickMobile] = useState("");
  let [city, pickCity] = useState("");
  let [pincode, pickPincode] = useState("");
  let [address, pickAddress] = useState("");

  const save = () => {
    toast("Please wait Processing....");
    let newdata = {
      fullname: name,
      emailid: email,
      mobileno: mobile,
      cityname: city,
      zipcode: pincode,
      fulladdress: address,
      tokenno: localStorage.getItem("tokenid"),
    };

    let url = "http://localhost:9999/customer";
    let postdata = {
      header: "Content-Type: application/json",
      method: "POST",
      body: JSON.stringify(newdata),
    };

    fetch(url, postdata)
      .then((responsedata) => responsedata.json())
      .then((response) => {
        if (response.error == "YES") {
          swal(response.message, "", "warning");
        } else {
          toast(response.message);
        }
      });
  };

  return (
    <div className="container mt-5">
      <ToastContainer />
      <div className="row">
        <div className="text-center mb-3">
          <h3 className="text-primary">
            <i class="fa-solid fa-user-plus"></i> Enter Customer Details{" "}
          </h3>
        </div>
        <div className="col-lg-4"></div>
        <div className="col-lg-4">
          <div className="row">
            <div className=" p-3 col-lg-12 ">
              <div className="card">
                <div className="card-header"> <h3>Details</h3></div>
                <div className="card-body">
                  
                </div>
                <div className="card-footer"></div>
              </div>
             
            </div>
          </div>
          <div className="col-lg-4"></div>
        </div>
      </div>
    </div>
  );
};

export default NewCustomer;
