import {useState, useEffect} from "react";


const Editproduct = () =>{
        let { productid } = useParams();
        let[text, updatetext] = useState("Update");
        let [Name, pickName] = useState("");
        let [Price, pickPrice] = useState("");
        let [Qty, pickQty] = useState("");
        let [Details, pickDetails] = useState("");
      
        const getdata = () => {
          let url = "http://localhost:9999/product/" + productid;
          let data = {
             "name": Name,
             "price": Price,
             "qty": Qty,
             "details": Details
          };

          let postdata = 
          {
            headers: { "Content-Type": "application/json" },
            method: "PATCH",
            body: JSON.stringify(data)
            };
          fetch(url, postdata)
            .then((responsedata) => responsedata.json())
            .then((response) => {
               pickName(response.name);
               pickPrice(response.price);
               pickQty
            });
        };
      
        const update = () => {
            updatetext("Please wait....");
          window.location.href = "#/productlist";
        };
      
        useEffect(() => {
          getdata();
        }, [1]);
      
        return (
          <div className="container">
            <div className="row">
              <div className="col-lg-12 text-center mt-5 mb-3">
                <h2 className="text-primary">Edit Details</h2>
              </div>
      
              <div className="col-lg-3"></div>
              <div className="col-lg-6">
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th>Name: </th>
                      <td><input type="text" value={Name} onChange={obj => pickName(obj.target.value)}/></td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <th>Email: </th>
                      <td><input type="text" value={Price} onChange={obj => pickPrice(obj.target.value)}/> </td>
                    </tr>
                    <tr>
                      <th>Mobile no: </th>
                      <td><input type="text" value={Qty} onChange={obj => pickQty(obj.target.value)}/></td>
                    </tr>
                    <tr>
                      <th> City: </th>
                      <td><input type="text" value={Details} onChange={obj => pickDetails(obj.target.value)}/></td>
                    </tr>
      
                    <tr className="text-center">
                      <td colSpan={3}>
                        <button className="btn btn-danger" onClick={update}>
                          {text}
                        </button>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div className="col-lg-3"></div>
            </div>
          </div>
        );
      };

export default Editproduct;
