import { useState} from "react";

const NewCustomer = () =>{
    let [name, pickName] = useState("");
    let [email, pickEmail] = useState("");
    let [mobile, pickMobile] = useState("");
    let [city, pickCity] = useState("");
    let [pincode, pickPincode] = useState("");
    let [address, pickAddress] = useState("");
    
   const save = () =>{
   let newdata = 
      {
        "fullname" : name,
        "emailid": email,
        "mobileno": mobile,
        "cityname":  city,
        "zipcode": pincode,
        "fulladdress": address,
        "tokenno": localStorage.getItem("tokenid")
      }

      let url = "http://localhost:9999/customer";
      let postdata = {
         headers: 'Content-Type: application/json',
         method: "POST",
         body : JSON.stringify(newdata)
      }

      fetch(url, newdata)
      .then(responsedata => responsedata.json())
      .then(response => {
          
      })
   }

    return(
        <div className="container mt-5">
             <div className="row">
                 <div className="col-lg-12 text-center mb-3">
                       <h3 className="text-primary"> Enter Customer Details </h3>
                 </div>
                 <div className="col-lg-4"></div>
                 <div className="col-lg-4 p-3 shadow-lg">
                    <div className="row">
                        <div className="col-lg-12">
                           <label>Name</label>
                           <input type="text" className="form-control" placeholder="Enter Your Name" onChange={obj => pickName(obj.target.value)} />
                        </div>

                        <div className="col-lg-12">
                           <label>Email</label>
                           <input type="email" className="form-control" placeholder="Enter Your Emailid" onChange={obj => pickEmail(obj.target.value)} />
                        </div>

                        <div className="col-lg-12">
                           <label>Mobile</label>
                           <input type="number" className="form-control" placeholder="Enter Your Mobile no." onChange={obj => pickMobile(obj.target.value)} />
                        </div>

                        <div className="col-lg-12">
                           <label>City</label>
                           <input type="text" className="form-control" placeholder="Enter Your City" onChange={obj => pickCity(obj.target.value)} />
                        </div>

                        <div className="col-lg-12">
                           <label>Pincode</label>
                           <input type="Number" className="form-control"  placeholder="Enter Your Pincode" onChange={obj => pickPincode(obj.target.value)} />
                        </div>

                        <div className="col-lg-12">
                           <label>Address</label>
                           <textarea type="text" className="form-control" placeholder="Enter Your Address" onChange={obj => pickAddress(obj.target.value)} rows={3}> </textarea> 
                        </div>
                    </div>
                     
                 </div>
                 <div className="col-lg-4"></div>
             </div>
        </div>
    )
    }
    export default NewCustomer;
