import { useEffect, useState } from "react";

const Productlist = () => {
  let [Products, productlist] = useState([]);
  let [keyword, updatekeyword] = useState("");

  const getallproduct = () => {
    let url =
      "http://localhost:9999/product/"+localStorage.getItem("tokenid");
    fetch(url)
      .then((responsedata) => responsedata.json())
      .then((response) => {
        productlist(response);
      });
  };

  useEffect(() => {
    getallproduct();
  }, [1]);

  return (
    <div className="container mt-5">
      <div className="row mb-5 mb-5">
        <div className="col-lg-3"></div>
        <div className="col-lg-6">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            onChange={(obj) => updatekeyword(obj.target.value)}
          />
        </div>
        <div className="col-lg-3"></div>
      </div>

      <div className="row">
        <div className="col-lg-12 text-center">  <caption className="text-primary"><h2></h2>Product List : {Products.length}</caption> </div>
        <div className="col-lg-12">
          <table
            className="table table-bordered "
            border="2px"
            cellPadding="5px"
            cellSpacing="1px"
          >
           
            <thead>
              <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Details</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {Products.map((product, index) => {
                if (
                  product.name.toLowerCase().includes(keyword.toLowerCase())
                ) {
                  return (
                    <tr key={index}>
                      <td>{product.name}</td>
                      <td>{product.price}</td>
                      <td>{product.qty}</td>
                      <td>{product.details}</td>
                      <td>
                        <button className="btn btn-danger">Edit</button>
                      </td>
                      <td>
                        <button className="btn btn-danger">Delete</button>
                      </td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};
export default Productlist;
