import {useState, useEffect} from 'react';

const Mybilling = () =>{
    let [Customers, customerlist] = useState([]);
    let [Products, productlist] = useState([]);
  
    const getallcustomer = () => {
      let url =
        "http://localhost:9999/customer/" + localStorage.getItem("tokenid");
      fetch(url)
        .then((responsedata) => responsedata.json())
        .then((response) => {
          customerlist(response);
        });
    };
  
    const getallproduct = () => {
      let url =
        "http://localhost:9999/product";
      fetch(url)
        .then((responsedata) => responsedata.json())
        .then((response) => {
          productlist(response);
        });
    };
  
    useEffect(() => {
      getallcustomer();
      getallproduct();
    }, [1]);

    return(
    <div className="container mt-5">
         <div className="row">
            <div className="col-lg-12 text-center">
                <h2 className="text-primary"> Billing Management </h2>
            </div>

            <div className="col-lg-2">
                <label> Select Customer</label>
                <select className='form-control'>
                    <option> Choose </option>
                      {
                         Customers.map((customer,index)=>{
                              return(
                                 <option key={index}> {customer.name} </option>
                              )
                         })
                      }
                </select>
            </div>

            <div className="col-lg-2">
                <label> Select Product</label>
                <select className='form-control'>
                    <option> Choose </option>
                      {
                         Products.map((product,index)=>{
                              return(
                                 <option key={index}> {product.name} </option>
                              )
                         })
                      }
                </select>
            </div>
         </div>
     </div>
    )
}

export default Mybilling;
