const AllCustomer = () =>{
   let [Customers, customerlist] = useState([]);

const getallcustomer = () =>{
    let url = "http://localhost:9999/customer/"+localStorage.getItem("tokenid");
    fetch(url)
    .then(responsedata => responsedata.json())
    .then(response => {
        customerlist(response);
    })
}

useEffect(() =>{
    getallcustomer();
}, [1])
   
    
