import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Productlist = () => {
  let [Products, productlist] = useState([]);
  let [keyword, updatekeyword] = useState("");

  const getallproduct = () => {
    let url =
      "http://localhost:9999/product/" + localStorage.getItem("tokenid");
    fetch(url)
      .then((responsedata) => responsedata.json())
      .then((response) => {
        productlist(response);
      });
  };

  useEffect(() => {
    getallproduct();
  }, [1]);

  return (
    <div className="container mt-5">
      <div className="row mb-5 mb-5">
        <div className="col-lg-3"></div>
        <div className="col-lg-6">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            onChange={(obj) => updatekeyword(obj.target.value)}
          />
        </div>
        <div className="col-lg-3"></div>
      </div>

      <div className="row">
        <div className="col-lg-12">
          <table
            className="table table-borderd table-hover"
            border="1px"
            cellPadding="5px"
            cellSpacing="1px"
          >
            <thead>
              <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Details</th>
              </tr>
            </thead>
            <tbody>
              {Products.map((product, index) => {
                if (
                  product.name.toLowerCase().includes(keyword.toLowerCase())
                ) {
                  return (
                    <tr key={index}>
                      <td>{product.name}</td>
                      <td>{product.email.substring(0, 10) + "..."}</td>
                      <td>{product.mobile}</td>
                      <td>{product.city}</td>
                      <td>{product.pincode}</td>
